const app = document.getElementById('app');
const cartBtn = document.getElementById('cartBtn');
const homeBtn = document.getElementById('homeBtn');
const searchInput = document.getElementById('searchInput');
const categoryFilter = document.getElementById('categoryFilter');
const sortFilter = document.getElementById('sortFilter');
const cartCount = document.getElementById('cartCount');
const yearEl = document.getElementById('year');
yearEl.textContent = new Date().getFullYear();

// Modal
const modal = document.getElementById('modal');
const modalBody = document.getElementById('modalBody');
const closeModal = document.querySelector('.close');
closeModal.onclick = () => modal.style.display = 'none';
window.onclick = e => { if(e.target === modal) modal.style.display = 'none'; };

// Sample products with reliable online images
const products = [
  {id:1,name:"Laptop",price:800,img:"data:image/jpeg;base64,"},
  {id:2,name:"Headphones",price:50,img:"data:image/jpeg;base64,",desc:"Noise cancelling headphones.",category:"Electronics"},
  {id:3,name:"Smartphone",price:500,img:"data:image/jpeg;base64,",desc:"Latest smartphone.",category:"Electronics"},
  {id:4,name:"Camera",price:300,img:"data:image/jpeg;base64,",desc:"DSLR camera.",category:"Electronics"},
  {id:5,name:"Chair",price:120,img:"data:image/jpeg;base64,",desc:"Comfortable chair.",category:"Furniture"},
  {id:6,name:"Table",price:200,img:"data:image/jpeg;base64,",desc:"Wooden dining table.",category:"Furniture"},
  {id:7,name:"Notebook",price:5,img:"data:image/jpeg;base64,",desc:"College notebook.",category:"Stationery"},
  {id:8,name:"Pen Set",price:10,img:"data:image/jpeg;base64,",desc:"Smooth gel pens.",category:"Stationery"}
];

let cart = JSON.parse(localStorage.getItem('cart')) || [];

// Render products
function renderProducts(list = products) {
  app.innerHTML = '';
  if(list.length === 0){ app.innerHTML='<p>No products found.</p>'; return; }
  list.forEach(p => {
    const card = document.createElement('div');
    card.className = 'product-card';
    card.innerHTML = `
      <img src="${p.img}" alt="${p.name}" loading="lazy">
      <h3>${p.name}</h3>
      <p>$${p.price}</p>
      <p>Category: ${p.category}</p>
      <button onclick="showDetails(${p.id})">View</button>
      <button onclick="addToCart(${p.id})">Add to Cart</button>
    `;
    app.appendChild(card);
  });
}

// Show modal
window.showDetails = id => {
  const product = products.find(p => p.id === id);
  modalBody.innerHTML = `
    <h2>${product.name}</h2>
    <img src="${product.img}" alt="${product.name}" style="width:100%;border-radius:5px;">
    <p>${product.desc}</p>
    <p>Price: $${product.price}</p>
    <p>Category: ${product.category}</p>
    <button onclick="addToCart(${product.id})">Add to Cart</button>
  `;
  modal.style.display = 'block';
};

// Add to cart
window.addToCart = id => {
  const product = products.find(p => p.id === id);
  const exists = cart.find(p => p.id === id);
  if(exists) exists.qty += 1;
  else cart.push({...product, qty:1});
  updateCart();
};

// Remove from cart
window.removeFromCart = id => {
  cart = cart.filter(item => item.id !== id);
  updateCart();
  renderCart();
};

// Render cart
function renderCart() {
  app.innerHTML = '';
  if(cart.length === 0){ app.innerHTML='<p>Your cart is empty.</p>'; return; }
  let totalPrice = 0;
  cart.forEach(item => {
    totalPrice += item.price * item.qty;
    const card = document.createElement('div');
    card.className = 'product-card';
    card.innerHTML = `
      <img src="${item.img}" alt="${item.name}" loading="lazy">
      <h3>${item.name}</h3>
      <p>Price: $${item.price}</p>
      <p>Qty: ${item.qty}</p>
      <button onclick="removeFromCart(${item.id})">Remove</button>
    `;
    app.appendChild(card);
  });
  const checkout = document.createElement('div');
  checkout.innerHTML = `<h3>Total: $${totalPrice}</h3><button onclick="checkoutCart()">Checkout</button>`;
  app.appendChild(checkout);
}

// Checkout
window.checkoutCart = () => {
  alert('Thank you for your purchase!');
  cart = [];
  updateCart();
  renderProducts();
};

// Update cart & localStorage
function updateCart() {
  localStorage.setItem('cart', JSON.stringify(cart));
  cartCount.textContent = cart.reduce((acc, i) => acc + i.qty, 0);
}

// Populate categories
function populateCategories() {
  const categories = [...new Set(products.map(p=>p.category))];
  categories.forEach(cat=>{
    const opt = document.createElement('option');
    opt.value = cat;
    opt.textContent = cat;
    categoryFilter.appendChild(opt);
  });
}

// Filtering & Sorting
function filterAndSort() {
  let list = products.filter(p=>p.name.toLowerCase().includes(searchInput.value.toLowerCase()));
  if(categoryFilter.value !== 'all') list = list.filter(p=>p.category === categoryFilter.value);
  switch(sortFilter.value){
    case 'priceAsc': list.sort((a,b)=>a.price-b.price); break;
    case 'priceDesc': list.sort((a,b)=>b.price-a.price); break;
    case 'nameAsc': list.sort((a,b)=>a.name.localeCompare(b.name)); break;
    case 'nameDesc': list.sort((a,b)=>b.name.localeCompare(a.name)); break;
  }
  renderProducts(list);
}

// Event listeners
cartBtn.addEventListener('click', renderCart);
homeBtn.addEventListener('click', () => renderProducts());
searchInput.addEventListener('input', filterAndSort);
categoryFilter.addEventListener('change', filterAndSort);
sortFilter.addEventListener('change', filterAndSort);

// Initialize
populateCategories();
updateCart();
renderProducts();
